package com.amky.week3;

public class SimpleForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i <=10;i=i+2){
			System.out.println(i);
		}
		System.out.println("Going backwards");
		for(int i=20; i>0;i--){
			System.out.println(i);
		}

	}

}
